DROP VIEW IF EXISTS `view_app_info_connection`;
CREATE ALGORITHM=UNDEFINED DEFINER=`dev`@`%` SQL SECURITY DEFINER VIEW `view_app_info_connection` AS select `c`.`id` AS `id`,`c`.`sort_id` AS `sort_id`,`c`.`app_id` AS `app_id`,`c`.`connection_id` AS `connection_id`,`cfg`.`name` AS `connection_name`,`cfg`.`connection_type` AS `connection_type`,`c`.`identity` AS `identity`,`c`.`is_default` AS `is_default`,`c`.`status` AS `status`,`c`.`remark` AS `remark`,`c`.`gmt_create` AS `gmt_create`,`c`.`gmt_modified` AS `gmt_modified` from (`app_info_connection` `c` join `cfg_connection` `cfg` on((`c`.`connection_id` = `cfg`.`id`))) ;

