DROP VIEW IF EXISTS `view_app_info_data_source`;
CREATE ALGORITHM=UNDEFINED DEFINER=`dev`@`%` SQL SECURITY DEFINER VIEW `view_app_info_data_source` AS select `d`.`id` AS `id`,`d`.`sort_id` AS `sort_id`,`d`.`app_id` AS `app_id`,`d`.`target_app_id` AS `target_app_id`,`a`.`app_name` AS `target_app_name`,`d`.`connection_id` AS `connection_id`,`c`.`name` AS `connection_name`,`c`.`connection_type` AS `connection_type`,`d`.`status` AS `status`,`d`.`remark` AS `remark`,`d`.`gmt_create` AS `gmt_create`,`d`.`gmt_modified` AS `gmt_modified` from ((`app_info_data_source` `d` join `cfg_connection` `c` on((`d`.`connection_id` = `c`.`id`))) join `app_info` `a` on((`d`.`target_app_id` = `a`.`id`))) ;

